with prev as (
    select * from information_schema.tables
    where table_schema = 'RAC_ACCNT'
    and last_ddl < current_date
)
select 'drop table '||table_name||';' as "--DROP SQL"
from prev;

/* for tables that have a consistent data type, the select * will work fine */
with curr as (
    select * from information_schema.tables
    where table_schema = 'RAC_ACCNT'
    and row_count> 0 
)
select 'CREATE OR REPLACE TABLE ' ||  regexp_replace( table_name, '_(\\d*)(\\d+?)_(\\d)$','') || ' AS ('  ||
    listagg( 'SELECT * FROM '|| table_name, ' UNION ALL ' )
        within group ( order by table_name ) ||'); ' as "--SQL"
from curr
group by regexp_replace( table_name, '_(\\d*)(\\d+?)_(\\d)$','')
having count( table_name) > 1
;

/* create a listagg of the columns converted to text */
with cols as (
select table_name, 
    listagg( column_name||':TEXT as '||column_name, ', ') 
        within group ( order by ordinal_position ) as cols
    from information_schema.columns
    group by table_name
)
select * from cols
where table_name like 'RA_CUSTOMER_TRX_ALL%';

/* combine the two queries */
with curr as (
    select * from information_schema.tables
    where table_schema = 'RAC_ACCNT'
    and row_count> 0 
),
cols as (
select table_name, 
    listagg( column_name||'::TEXT as '||column_name, ', ') 
        within group ( order by ordinal_position ) as cols
    from information_schema.columns
    group by table_name
)
select 'CREATE OR REPLACE TABLE ' ||  regexp_replace( curr.table_name, '_(\\d*)(\\d+?)_(\\d)$','') || ' AS ('  ||
    listagg( 'SELECT '||cols.cols||'  FROM '|| curr.table_name, ' UNION ALL ' )
        within group ( order by curr.table_name ) ||'); ' as "--SQL"
from curr
    inner join cols on (curr.table_name = cols.table_name)
group by regexp_replace( curr.table_name, '_(\\d*)(\\d+?)_(\\d)$','')
having count( curr.table_name) > 1
;


with issues as (
select table_name, regexp_replace( table_name, '_(\\d*)(\\d+?)_(\\d)$','') as final_table, column_name, data_type, ordinal_position
from information_schema.columns
where table_schema = 'RAC_ACCNT'
    -- and table_name like 'RA_CUST_TRX_LINE_GL_DIST_ALL%'
    and table_name like 'RA_CUSTOMER_TRX_ALL%'
)
select final_table, column_name, ordinal_position, 
    max( case when data_type in ('FLOAT','NUMBER') then 'X' else null end ) as NUM,
    max( case when data_type = 'TEXT' then 'X' else null end ) as TXT,
    max( case when data_type not in ('FLOAT','NUMBER', 'TEXT')  then 'X' else null end ) as OTHR
from issues
group by 1,2,3
order by 1,3;

